mapper.select:
select count(*)
from examinfo
where eNum=#{eNum}

mapper.insert:
insert into examinfo (eNum, eName, eTime, eType, eWork, eOrgan, eLevel, ePeople, eScore, courseID,
courseName)
values
(
    #{eNum},#{eName},#{eTime},#{eType},#{eWork},#{eOrgan},#{eLevel},#{ePeople},#{eScore},#{courseID},#{courseName}
)
